TEST_INPUT = "ABBAC"

INPUT = ""

from collections import Counter


POTIONS_PER_CREATURE = {
    "A": 0,
    "B": 1,
    "C": 3,
}


def compute_total_potions(input):
    creatures_freq = Counter(input)
    return creatures_freq["B"] + creatures_freq["C"] * POTIONS_PER_CREATURE["C"]


assert compute_total_potions(TEST_INPUT) == 5

print(compute_total_potions(INPUT))
